window.toggleTheme=function(){const root=document.documentElement;const currentTheme=root.classList.contains("light-theme")?"light":"dark";if(currentTheme==="dark"){root.classList.remove("dark-theme");root.classList.add("light-theme");localStorage.setItem("theme","light");}else{root.classList.remove("light-theme");root.classList.add("dark-theme");localStorage.setItem("theme","dark");}};window.addEventListener("DOMContentLoaded",function(){const savedTheme=localStorage.getItem("theme")||"dark";const root=document.documentElement;if(savedTheme==="light"){root.classList.add("light-theme");}else{root.classList.add("dark-theme");}});window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",function(e){if(!localStorage.getItem("theme")){const root=document.documentElement;if(e.matches){root.classList.remove("light-theme");root.classList.add("dark-theme");}else{root.classList.remove("dark-theme");root.classList.add("light-theme");}}});